import React, { useState, useEffect } from 'react';
import './TeaStyle.css'; // Adjust if necessary

const StudentEvaluation = () => {
    const [rollNum, setRollNum] = useState('');
    const [evaluations, setEvaluations] = useState([]);
    const [feedback, setFeedback] = useState('');
    const [teacherName, setTeacherName] = useState('');
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    // Handle change in roll number
    const handleRollNumChange = (e) => {
        setRollNum(e.target.value);
        setEvaluations([]);
        setError('');
        setMessage('');
    };

    // Fetch evaluations when roll number is entered
    useEffect(() => {
        if (rollNum) {
            const fetchEvaluations = async () => {
                try {
                    const response = await fetch(`http://localhost:5000/api/student/${rollNum}/evaluations`);
                    const json = await response.json();

                    if (response.ok) {
                        setEvaluations(json);
                    } else {
                        setError('Failed to fetch evaluations.');
                    }
                } catch (err) {
                    setError('Error fetching evaluations.');
                }
            };

            fetchEvaluations();
        }
    }, [rollNum]);

    // Handle adding a new evaluation
    const handleAddEvaluation = async (e) => {
        e.preventDefault();

        if (!feedback || !teacherName) {
            setError('Both feedback and teacher name are required');
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/api/student/${rollNum}/evaluations`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ feedback, teacherName }),
            });

            const json = await response.json();

            if (response.ok) {
                setEvaluations([...evaluations, json]);
                setFeedback('');
                setTeacherName('');
                setMessage('Evaluation added successfully');
            } else {
                setError('Failed to add evaluation');
            }
        } catch (err) {
            setError('Error adding evaluation');
        }
    };

    // Handle updating a specific evaluation
    const handleUpdateEvaluation = async (index) => {
        const updatedFeedback = prompt('Enter new feedback:', evaluations[index].feedback);
        const updatedTeacherName = prompt('Enter new teacher name:', evaluations[index].teacherName);

        if (!updatedFeedback || !updatedTeacherName) return;

        try {
            const response = await fetch(`http://localhost:5000/api/student/${rollNum}/evaluations/${index}`, {
                method: 'PATCH',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ feedback: updatedFeedback, teacherName: updatedTeacherName }),
            });

            if (response.ok) {
                const updatedEvaluations = [...evaluations];
                updatedEvaluations[index] = { feedback: updatedFeedback, teacherName: updatedTeacherName };
                setEvaluations(updatedEvaluations);
                setMessage('Evaluation updated successfully');
            } else {
                setError('Failed to update evaluation');
            }
        } catch (err) {
            setError('Error updating evaluation');
        }
    };

    // Handle deleting a specific evaluation
    const handleDeleteEvaluation = async (index) => {
        try {
            const response = await fetch(`http://localhost:5000/api/student/${rollNum}/evaluations/${index}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setEvaluations(evaluations.filter((_, i) => i !== index));
                setMessage('Evaluation deleted successfully');
            } else {
                setError('Failed to delete evaluation');
            }
        } catch (err) {
            setError('Error deleting evaluation');
        }
    };

    // Handle deleting all evaluations
    const handleDeleteAllEvaluations = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/student/${rollNum}/evaluations`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setEvaluations([]);
                setMessage('All evaluations deleted successfully');
            } else {
                setError('Failed to delete evaluations');
            }
        } catch (err) {
            setError('Error deleting evaluations');
        }
    };

    return (
        <div className="evaluation-container">
            <h2>Student Evaluation</h2>

            <div className="input-group">
                <label>Enter Roll Number:</label>
                <input
                    type="number"
                    value={rollNum}
                    onChange={handleRollNumChange}
                    placeholder="Enter roll number"
                    required
                />
            </div>

            <div className="evaluation-list">
                <h3>Current Evaluations:</h3>
                {evaluations.length > 0 ? (
                    <ul>
                        {evaluations.map((evalItem, index) => (
                            <li key={index} className="evaluation-item">
                                <span><strong>Feedback:</strong> {evalItem.feedback}</span>
                                <span><strong>Teacher:</strong> {evalItem.teacherName}</span>
                                <button onClick={() => handleUpdateEvaluation(index)}>Edit</button>
                                <button onClick={() => handleDeleteEvaluation(index)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No evaluations found.</p>
                )}
            </div>

            <div className="input-group">
                <label>Feedback:</label>
                <input
                    type="text"
                    value={feedback}
                    onChange={(e) => setFeedback(e.target.value)}
                    placeholder="Enter feedback"
                />
            </div>

            <div className="input-group">
                <label>Teacher Name:</label>
                <input
                    type="text"
                    value={teacherName}
                    onChange={(e) => setTeacherName(e.target.value)}
                    placeholder="Enter teacher's name"
                />
            </div>

            <button onClick={handleAddEvaluation}>Add Evaluation</button>

            <div className="buttons">
                <button onClick={handleDeleteAllEvaluations}>Delete All Evaluations</button>
            </div>

            {error && <div className="error">{error}</div>}
            {message && <div className="message">{message}</div>}
        </div>
    );
};

export default StudentEvaluation;
