import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import './StudentProfile.css';
import { Link, useNavigate } from 'react-router-dom';

const StudentProfile = () => {
    const { rollNum: paramRollNum } = useParams();
    const storedRollNum = localStorage.getItem('rollNum');
    const rollNum = paramRollNum || storedRollNum || '';

    const [student, setStudent] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        if (!rollNum) {
            setError('Roll number not found.');
            setLoading(false);
            return;
        }

        const fetchStudentProfile = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/student/profile/${rollNum}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${localStorage.getItem('studentToken')}`
                    },
                });

                if (!response.ok) {
                    const errorText = await response.text();
                    throw new Error(errorText || 'Failed to fetch profile');
                }

                const data = await response.json();
                setStudent(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchStudentProfile();
    }, [rollNum]);

    if (loading) return <Typography>Loading...</Typography>;
    if (error) return <Typography color="error">{error}</Typography>;
    if (!student) return <Typography>No student data found.</Typography>;

    return (
        <div>
        <div className="sidebar">
        <h2>Student Panel</h2>
        <ul>
          <li><Link to={`/student/StudentDashboard/${rollNum}`}>Dashboard</Link></li>
          <li><Link to={`/student/profile/${rollNum}`}>Profile</Link></li>
          <li><Link to="/Teacher">Teachers</Link></li>
          <li><Link to="/Class">Class</Link></li>
          <li><Link to="/Subject">Subject</Link></li>
          <li><Link to="/Notice">Notice</Link></li>
        </ul>
      </div>
        <Container>
            <Typography variant="h4">Student Profile</Typography>
            <Paper elevation={3} sx={{ padding: '20px', marginBottom: '20px' }}>
                <Typography variant="h6">Basic Information</Typography>
                <Typography>Name: {student.name}</Typography>
                <Typography>Roll Number: {student.rollNum}</Typography>
                <Typography>Class: {student.sclassName}</Typography>
                <Typography>Role: {student.role}</Typography>
            </Paper>

            <Typography variant="h5">Exam Results</Typography>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Subject</TableCell>
                            <TableCell>Marks</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {student.examResult.map((result, index) => (
                            <TableRow key={index}>
                                <TableCell>{result.subName}</TableCell>
                                <TableCell>{result.marksObtained}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            <Typography variant="h5">Attendance</Typography>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Date</TableCell>
                            <TableCell>Status</TableCell>
                            <TableCell>Subject</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {student.attendance.map((record, index) => (
                            <TableRow key={index}>
                                <TableCell>{new Date(record.date).toLocaleDateString()}</TableCell>
                                <TableCell>{record.status}</TableCell>
                                <TableCell>{record.subName}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Container>
        </div>
    );
};

export default StudentProfile;
