import React, { useEffect, useState } from 'react';
import ClassForm from '../../components/ClassForm'; 
import ClassDetails from '../../components/ClassDetails';
import Sidebar from '../../components/AdminNav';

const AddSclassPage = () => {
    const [classDetails, setClassDetails] = useState(null);

    useEffect(() => {
        const fetchClassDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/sclass`);
                const json = await response.json();
    
                if (response.ok) {
                    setClassDetails(json);
                } else {
                    console.error('Failed to fetch class details:', json);
                }
            } catch (error) {
                console.error('Error fetching class details:', error);
            }
        }; 

        fetchClassDetails()
    }, [])
    

    return (
        <div>
            <Sidebar />
            
            <ClassForm/>
            <div>
            <h2>Class Details</h2>
            {/* <AddSclassForm onClassAdded={handleClassAdded} /> */}
            <div className='class'>
                {classDetails && classDetails.map((classDetail) => (
                    <ClassDetails key={classDetail._id} classDetail ={classDetail}/>
                ))}
            </div>
            
        </div>
        </div>
    );
};

export default AddSclassPage;
