import React, { useEffect, useState } from 'react';
import StudentDetails from '../../components/StudentDetails';
import StudentForm from '../../components/StudentForm';
import './style.css';
import Sidebar from '../../components/AdminNav';

const StudentPage = () => {
    const [students, setStudents] = useState([]);
    const [selectedStudent, setSelectedStudent] = useState(null); // Track selected student for update

    // Fetch all students
    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/student');
                const data = await response.json();
                if (response.ok) {
                    setStudents(data);
                } else {
                    console.error('Failed to fetch students:', data);
                }
            } catch (error) {
                console.error('Error fetching students:', error);
            }
        };

        fetchStudents();
    }, []);

    // Handle delete student
    const handleDelete = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/student/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setStudents(students.filter((student) => student._id !== id));
            } else {
                console.error('Failed to delete student');
            }
        } catch (error) {
            console.error('Error deleting student:', error);
        }
    };

    // Handle update button click
    const handleUpdateClick = (student) => {
        setSelectedStudent(student); // Set the selected student for update
    };

    // Handle form submission for update
    const handleUpdateSubmit = async (updatedStudent) => {
        try {
            const response = await fetch(`http://localhost:5000/api/student/${updatedStudent._id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedStudent),
            });

            if (response.ok) {
                const updatedData = await response.json();
                // Update the student in the state
                setStudents(students.map((student) =>
                    student._id === updatedData._id ? updatedData : student
                ));
                setSelectedStudent(null); // Close the form after update
            } else {
                console.error('Failed to update student:', await response.json());
            }
        } catch (error) {
            console.error('Error updating student:', error);
        }
    };

    return (
        <div>
            <Sidebar />
            <div>
                <StudentForm />
                <div className='stu'>
                    <h2>Student Details</h2>
                    <div className="student-list">
                        {students.map((student) => (
                            <StudentDetails
                                key={student._id}
                                student={student}
                                onDelete={handleDelete}
                                onUpdate={() => handleUpdateClick(student)} // Pass the student to update
                            />
                        ))}
                    </div>

                    {/* Update Form */}
                    {selectedStudent && (
                        <div className="update-form">
                            <h3>Update Student</h3>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    const formData = new FormData(e.target);
                                    const updatedStudent = {
                                        _id: selectedStudent._id,
                                        name: formData.get('name'),
                                        Fname: formData.get('Fname'),
                                        Mname: formData.get('Mname'),
                                        mobile: formData.get('mobile'),
                                        rollNum: formData.get('rollNum'),
                                        sclassName: formData.get('sclassName'),
                                        gender: formData.get('gender'),
                                    };
                                    handleUpdateSubmit(updatedStudent);
                                }}
                            >
                                <label>
                                    Name:
                                    <input
                                        type="text"
                                        name="name"
                                        defaultValue={selectedStudent.name}
                                        required
                                    />
                                </label>
                                <label>
                                    Father's Name:
                                    <input
                                        type="text"
                                        name="Fname"
                                        defaultValue={selectedStudent.Fname}
                                    />
                                </label>
                                <label>
                                    Mother's Name:
                                    <input
                                        type="text"
                                        name="Mname"
                                        defaultValue={selectedStudent.Mname}
                                    />
                                </label>
                                <label>
                                    Mobile:
                                    <input
                                        type="text"
                                        name="mobile"
                                        defaultValue={selectedStudent.mobile}
                                    />
                                </label>
                                <label>
                                    Roll Number:
                                    <input
                                        type="text"
                                        name="rollNum"
                                        defaultValue={selectedStudent.rollNum}
                                        required
                                    />
                                </label>
                                <label>
                                    Class:
                                    <input
                                        type="text"
                                        name="sclassName"
                                        defaultValue={selectedStudent.sclassName}
                                        required
                                    />
                                </label>
                                <label>
                                    Gender:
                                    <input
                                        type="text"
                                        name="gender"
                                        defaultValue={selectedStudent.gender}
                                    />
                                </label>
                                <button type="submit">Save</button>
                                <button type="button" onClick={() => setSelectedStudent(null)}>
                                    Cancel
                                </button>
                            </form>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default StudentPage;
