import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [studentCount, setStudentCount] = useState(0);

  useEffect(() => {
    const fetchStudentCount = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/student/count');
        const data = await response.json();
        if (response.ok) {
          setStudentCount(data.totalStudents); // Ensure your backend returns `{ totalStudents: count }`
        } else {
          console.error('Failed to fetch student count:', data.error);
        }
      } catch (error) {
        console.error('Error fetching student count:', error);
      }
    };

    fetchStudentCount();
  }, []);

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Panel</h2>
        <ul>
          <li><Link to="/admin/AdminDashboard">Dashboard</Link></li>
          <li><Link to="/admin/AdminDashboard/StudentPage">Students</Link></li>
          <li><Link to="/admin/AdminDashboard/TeacherPage">Teachers</Link></li>
          <li><Link to="/admin/AdminDashboard/AddSclassPage">Class</Link></li>
          <li><Link to="/admin/AdminDashboard/SubjectPage">Subject</Link></li>
          <li><Link to="/admin/AdminDashboard/NoticePage">Notice</Link></li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header>
          <h1>Welcome, Admin!</h1>
          <button className="logout-btn">Logout</button>
        </header>

        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>Total Students</h3>
            <p>{studentCount}</p> {/* Dynamic count */}
          </div>
          <div className="stat-card">
            <h3>Total Teachers</h3>
            <p>75</p>
          </div>
          <div className="stat-card">
            <h3>Total Classes</h3>
            <p>30</p>
          </div>
        </div>

        <div className="recent-activity">
          <h2>Recent Activity</h2>
          <ul>
            <li>New student registered: John Doe</li>
            <li>Class 10A schedule updated</li>
            <li>Teacher Jane Smith added a new assignment</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
