import React, { useEffect, useState } from 'react';
import TeacherForm from '../../components/TeacherForm'; 
import TeacherDetails from '../../components/TeacherDetails';
import './style.css';
import Sidebar from '../../components/AdminNav'

const TeacherPage = () => {
    const [teacher, setTeacher] = useState(null);

    useEffect(() => {
        const fetchTeacher = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/teacher`);
                const json = await response.json();
    
                if (response.ok) {
                    setTeacher(json);
                } else {
                    console.error('Failed to fetch teacher details:', json);
                }
            } catch (error) {
                console.error('Error fetching teacher details:', error);
            }
        }; 

        fetchTeacher();
    }, []);
    

    return (
        <div>
            <Sidebar />
            <div>
            <TeacherForm />
            <div className='tea'>
            <h2>Teacher Details</h2>
            <div className='class'>
                {teacher && teacher.map((item) => (
                    <TeacherDetails key={item._id} teacher={item} />
                ))}
            </div>
            </div>
        </div>
        </div>
    );
};

export default TeacherPage;
