import React from 'react';

const TeacherDetails = ({ teacher, onDelete, onUpdate }) => {
    return (
        <div className="teacher-details">
            <p><strong>Name:</strong> {teacher.name}</p>
            <p><strong>Email:</strong> {teacher.email}</p>
            <p><strong>Subject:</strong> {teacher.teachSubject}</p>
            <p><strong>Date of Birth:</strong> {teacher.dob ? new Date(teacher.dob).toLocaleDateString() : 'N/A'}</p>
            <p><strong>Join Date:</strong> {teacher.JoinDate ? new Date(teacher.JoinDate).toLocaleDateString() : 'N/A'}</p>
            <p><strong>Mobile:</strong> {teacher.mobile || 'N/A'}</p>
            <p><strong>Address:</strong> {teacher.address || 'N/A'}</p>
            <button onClick={onUpdate}>Update</button>
            <button onClick={onDelete}>Delete</button>
        </div>
    );
};

export default TeacherDetails;
