import React, { useState } from 'react';
import AddSclassForm from '../components/AddSclassForm'; // Correct import path

const AddSclassPage = () => {
    const [classDetails, setClassDetails] = useState(null);

    const handleClassAdded = (details) => {
        setClassDetails(details);
    };

    return (
        <div>
            <h2>Add New Class</h2>
            <AddSclassForm onClassAdded={handleClassAdded} />
            {classDetails && (
                <div>
                    <h3>Class Details:</h3>
                    <p>Name: {classDetails.sclassName}</p>
                </div>
            )}
        </div>
    );
};

export default AddSclassPage;
