import React, { useEffect, useState } from 'react';
import ClassForm from '../../components/ClassForm';
import ClassDetails from '../../components/ClassDetails';
import Sidebar from '../../components/AdminNav';
import './style.css';

const AddSclassPage = () => {
    const [classDetails, setClassDetails] = useState([]);
    const [selectedClass, setSelectedClass] = useState(null); // For update functionality

    // Fetch all classes
    useEffect(() => {
        const fetchClassDetails = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/sclass');
                const json = await response.json();

                if (response.ok) {
                    setClassDetails(json);
                } else {
                    console.error('Failed to fetch class details:', json);
                }
            } catch (error) {
                console.error('Error fetching class details:', error);
            }
        };

        fetchClassDetails();
    }, []);

    // Handle delete class
    const handleDelete = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/sclass/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                // Remove the deleted class from the state
                setClassDetails(classDetails.filter((classDetail) => classDetail._id !== id));
            } else {
                console.error('Failed to delete class');
            }
        } catch (error) {
            console.error('Error deleting class:', error);
        }
    };

    // Handle update class
    const handleUpdate = async (id, updatedData) => {
        try {
            const response = await fetch(`http://localhost:5000/api/sclass/${id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedData),
            });

            if (response.ok) {
                const updatedClass = await response.json();
                // Update the class in the state
                setClassDetails(classDetails.map((classDetail) =>
                    classDetail._id === id ? updatedClass : classDetail
                ));
                setSelectedClass(null); // Close the update form
            } else {
                console.error('Failed to update class:', await response.json());
            }
        } catch (error) {
            console.error('Error updating class:', error);
        }
    };

    return (
        <div>
            <Sidebar />
            <div>
                <ClassForm />
                <div className='class'>
                    <h2>Class Details</h2>
                    <div className='class-list'>
                        {classDetails.map((classDetail) => (
                            <ClassDetails
                                key={classDetail._id}
                                classDetail={classDetail}
                                onDelete={() => handleDelete(classDetail._id)}
                                onUpdate={() => setSelectedClass(classDetail)} // Pass the class to update
                            />
                        ))}
                    </div>

                    {/* Update Form */}
                    {selectedClass && (
                        <div className="update-form">
                            <h3>Update Class</h3>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    const formData = new FormData(e.target);
                                    const updatedData = {
                                        sclassName: formData.get('sclassName'),
                                    };
                                    handleUpdate(selectedClass._id, updatedData);
                                }}
                            >
                                <label>
                                    Class Name:
                                    <input
                                        type="text"
                                        name="sclassName"
                                        defaultValue={selectedClass.sclassName}
                                        required
                                    />
                                </label>
                                <button type="submit">Save</button>
                                <button type="button" onClick={() => setSelectedClass(null)}>
                                    Cancel
                                </button>
                            </form>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AddSclassPage;
