import React, { useEffect, useState } from 'react';
import NoticeForm from '../../components/NoticeForm'; 
import NoticeDetails from '../../components/NoticeDetails';
import './style.css';
import Sidebar from '../../components/AdminNav';

const NoticePage = () => {
    const [notice, setNotice] = useState(null);

    useEffect(() => {
        const fetchNotice = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/notice`);
                const json = await response.json();
    
                if (response.ok) {
                    setNotice(json);
                } else {
                    console.error('Failed to fetch notice details:', json);
                }
            } catch (error) {
                console.error('Error fetching notice details:', error);
            }
        }; 

        fetchNotice();
    }, []);
    

    return (
        <div>
            <Sidebar />
            <h2 className='notice-h2'>Notices</h2>
            <div className='class'>
                {notice && notice.map((item) => (
                    <NoticeDetails key={item._id} notice={item} />
                ))}
            </div>
            <NoticeForm />
        </div>
    );
};

export default NoticePage;
