import { useState } from "react";
import './form.css';

const TeacherForm = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [teachSubject, setTeachSubject] = useState('');
    const [teachSclass, setTeachSclass] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const teacher = { name, email, password, teachSubject, teachSclass };

        const response = await fetch('http://localhost:5000/api/teacher', {
            method: 'POST',
            body: JSON.stringify(teacher),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const json = await response.json();

        if (!response.ok) {
            setError(json.error);
        }
        if (response.ok) {
            setName('');
            setEmail('');
            setPassword('');
            setTeachSubject('');
            setTeachSclass('');
            setError(null);
            console.log('New teacher added', json);
        }
    };

    return (
        <form className="teacher-form" onSubmit={handleSubmit}>
            <h3>Add a new teacher</h3>

            <label>Teacher Name</label>
            <input
                type="text"
                onChange={(e) => setName(e.target.value)}
                value={name}
                required
            />

            <label>Email</label>
            <input
                type="email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
                required
            />

            <label>Password</label>
            <input
                type="password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
                required
            />

            <label>Teaching Subject</label>
            <input
                type="text"
                onChange={(e) => setTeachSubject(e.target.value)}
                value={teachSubject}
                required
            />

            <label>Teaching Class (ID)</label>
            <input
                type="text"
                onChange={(e) => setTeachSclass(e.target.value)}
                value={teachSclass}
                required
            />

            <button type="submit">Add Teacher</button>
            {error && <div className="error">{error}</div>}
        </form>
    );
};

export default TeacherForm;
