import React, { useState } from 'react';
import AddSclassForm from '../../components/AddSclassForm'; 

const AddSclassPage = () => {
    const [classDetails, setClassDetails] = useState(null);

    const handleClassAdded = async (details) => {
        await fetchClassDetails(details._id); // Fetch class details using the ID
    };

    const fetchClassDetails = async (id) => {
        try {
            const response = await fetch(`/sclass/${id}`);
            const json = await response.json();

            if (response.ok) {
                setClassDetails(json);
            } else {
                console.error('Failed to fetch class details:', json);
            }
        } catch (error) {
            console.error('Error fetching class details:', error);
        }
    };

    return (
        <div>
            <h2>Add New Class</h2>
            <AddSclassForm onClassAdded={handleClassAdded} />
            {classDetails && (
                <div>
                    <h3>Class Details:</h3>
                    <p>Name: {classDetails.sclassName}</p>
                    <p>ID: {classDetails._id}</p>
                    <p>Created At: {new Date(classDetails.createdAt).toLocaleString()}</p>
                </div>
            )}
        </div>
    );
};

export default AddSclassPage;
