import React, { useEffect, useState } from 'react';
import ComplainDetailsV from '../../components/ComplainDetailsV';
import './StuStyle.css';
import { Link, useParams } from 'react-router-dom';

const ViewComplain = () => {
    const [complains, setComplains] = useState([]);

    const { rollNum: paramRollNum } = useParams();
    const storedRollNum = localStorage.getItem('rollNum');
    const rollNum = paramRollNum || storedRollNum || '';

    // Fetch all complains
    useEffect(() => {
        const fetchComplains = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/complain');
                const json = await response.json();

                if (response.ok) {
                    setComplains(json);
                } else {
                    console.error('Failed to fetch complain details:', json);
                }
            } catch (error) {
                console.error('Error fetching complain details:', error);
            }
        };

        fetchComplains();
    }, []);

    return (
        <div>
            <div className="sidebar">
                <h2>Student Panel</h2>
                <ul>
                    <li><Link to={`/student/StudentDashboard/${rollNum}`}>Dashboard</Link></li>
                    <li><Link to={`/student/profile/${rollNum}`}>Profile</Link></li>
                    <li><Link to="/Teacher">Teachers</Link></li>
                    <li><Link to="/Class">Class</Link></li>
                    <li><Link to="/Subject">Subject</Link></li>
                    <li><Link to="/student/ViewComplain">Complaints</Link></li>
                </ul>
            </div>
            <div>
                <h2 className='complain-h2'>Complaints</h2>
                <div className='complain'>
                    {complains.map((complain) => (
                        <ComplainDetailsV
                            key={complain._id}
                            complain={complain}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default ViewComplain;
