import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';

const StudentProfile = () => {
    const { rollNum: paramRollNum } = useParams();
    const storedRollNum = localStorage.getItem('rollNum');
    const rollNum = paramRollNum ? paramRollNum : storedRollNum ? storedRollNum : null;

    if (!rollNum) {
        console.error("Roll Number is null. Redirecting...");
        return <Typography color="error">Invalid Roll Number. Please log in again.</Typography>;
    }


    const [student, setStudent] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {

        const fetchStudentProfile = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/student/profile/${rollNum}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${localStorage.getItem('studentToken')}`
                    },
                });

                const text = await response.text(); // Get the raw response as text
                console.log("Raw Server Response:", text); // Log the raw response

                try {
                    const data = JSON.parse(text); // Try to parse the text as JSON
                    if (!response.ok) {
                        throw new Error(data.message || 'Failed to fetch student profile');
                    }

                    setStudent(data);
                    setLoading(false);
                } catch (err) {
                    setError(err.message);
                    setLoading(false);
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchStudentProfile();
    }, [rollNum]);

    if (loading) {
        return <Typography>Loading...</Typography>;
    }

    if (error) {
        return <Typography color="error">{error}</Typography>;
    }

    if (!student) {
        return <Typography>No student data found.</Typography>;
    }

    return (
        <Container>
            <Typography variant="h4" gutterBottom>Student Profile</Typography>
            <Paper elevation={3} sx={{ padding: '20px', marginBottom: '20px' }}>
                <Typography variant="h6">Basic Information</Typography>
                <Typography>Name: {student.name}</Typography>
                <Typography>Roll Number: {student.rollNum}</Typography>
                <Typography>Class: {student.sclassName}</Typography>
                <Typography>Role: {student.role}</Typography>
            </Paper>

            <Typography variant="h5" gutterBottom>Exam Results</Typography>
            <TableContainer component={Paper} elevation={3} sx={{ marginBottom: '20px' }}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Subject</TableCell>
                            <TableCell>Marks Obtained</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {student.examResult.map((result, index) => (
                            <TableRow key={index}>
                                <TableCell>{result.subName}</TableCell>
                                <TableCell>{result.marksObtained}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            <Typography variant="h5" gutterBottom>Attendance</Typography>
            <TableContainer component={Paper} elevation={3}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Date</TableCell>
                            <TableCell>Status</TableCell>
                            <TableCell>Subject</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {student.attendance.map((record, index) => (
                            <TableRow key={index}>
                                <TableCell>{new Date(record.date).toLocaleDateString()}</TableCell>
                                <TableCell>{record.status}</TableCell>
                                <TableCell>{record.subName}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Container>
    );
};

export default StudentProfile;
